script:
async function callAPI(options) {
  return new Promise((resolve, reject) => {
    frappe.call({
      ...options,
      callback: async function (response) {
        resolve(response?.message || response?.value)
      }
    });
  })
}

callAPI({
  method: 'aisect.services.api.candidate_placement_ging',
  freeze: true,
  freeze_message: __("Getting"),
}).then(forms_lists => { 
    let card=''
    forms_lists.map((e)=>{

     card+= `<div class="col-lg-3 col-xs-12 col-sm-6 col-md-4 py-2">
          <a href="/app/query-report/${e.url}" class="card border rounded-lg" style="height: 84px; font-size: 11px; color:#525252; text-decoration: none; flex: 1;">
            <div class="p-3">
              <div style="font-size: 11.5px; font-weight: 550;">${e.name}</div>
              <div class="data"  style="font-size: 20px; font-weight: bold;">${e.value}</div>
            </div>
          </a>
        </div>`
    })
    var fpo_count = root_element.getElementById('fpo_count').innerHTML=card;
    fpo_count.innerHTML =  forms_lists !== undefined ? forms_lists[0].candidate_count : 0; 
});

const get_permission = async (user) => {
    let list = await callAPI({
        method: 'aisect.services.api.user_assign_geography',
        freeze: true,
        args: {
            doctype: "User Permission",
            user: user,
            view: "List",
            order_by: "",
            group_by: '',
        },

        freeze_message: __("Getting Permissions"),
    });
    return list;
};

const rander_html = async ()=>{
    let user=frappe.user.name;
    let list = await get_permission(user);
    let blocks = `<nav aria-label="breadcrumb1" class="d-flex align-items-center justify-content-between">
            <ol class="breadcrumb1" >`;
    for (let i = 0; i < list?.length; i++) {
        blocks = blocks +
       ` <li class="breadcrumb-item1 active" aria-current="page">${list?.[i].allow} :- ${list?.[i].name_value}</li> `
    }
    blocks = blocks + `</ol><p class="user_name_a">${frappe.session.user_fullname}</p></nav>`;
    root_element.getElementById('geo_data').innerHTML = list?.length ? blocks : '';
};

rander_html();

style:
