    display: flex;
    list-style: none;
    padding: 0;
    margin: 0;
    background-color: transparent;
}

.breadcrumb-item1 {
    margin-right: 0.5rem;
}

.breadcrumb-item1 + .breadcrumb-item1::before {
    content: "/";
