async function callAPI(options) {
  return new Promise((resolve, reject) => {
    frappe.call({
      ...options,
      callback: async function (response) {
        resolve(response?.message || response?.value)
      }
    });
  })
}

callAPI({
  method: 'aisect.services.api.candidate_placement_ging',
  freeze: true,
  freeze_message: __("Getting"),
}).then(forms_lists => {

    var fpo_count = root_element.getElementById('fpo_count');
    fpo_count.innerHTML =  forms_lists !== undefined ? forms_lists[0].candidate_count : 0; 
});

const get_permission = async (user) => {
    let list = await callAPI({
        method: 'aisect.services.api.user_assign_geography',
        freeze: true,
        args: {
            doctype: "User Permission",
            user: user,
            view: "List",
            order_by: "",
            group_by: '',
        },

        freeze_message: __("Getting Permissions"),
    });
    return list;
};

const rander_html = async ()=>{
    let user=frappe.user.name;
    let list = await get_permission(user);
    let blocks = `<nav aria-label="breadcrumb">
            <ol class="breadcrumb" >`;
    for (let i = 0; i < list?.length; i++) {
        blocks = blocks +
       ` <li class="breadcrumb-item active" aria-current="page">${list?.[i].allow}-${list?.[i].name_value}</li> `
    }
    blocks = blocks + `</ol></nav>`;
    root_element.getElementById('geo_data').innerHTML = list?.length ? blocks : '';
};

rander_html();

async function callAPI(options) {
    return new Promise((resolve, reject) => {
        frappe.call({
            ...options,
            callback: async function (response) {
                resolve(response?.message || response?.value)
            }
        });
    });
}

callAPI({
    method: 'aisect.services.api.success_story_data',
    freeze: true,
    freeze_message: __("Getting"),
}).then(forms_lists => {
    console.log(forms_lists);
    var card_titles = ``;
    forms_lists.forEach((item) => {
        card_titles += `
            <div>
                <div class="card" style="min-width: 380px; width:380px; min-height:180px;">
                <div class="card-body no-gutters">
                    <div class="d-flex align-items-center" style="gap:20px;">
                        <div class="">
                      <img src="${item?.candidate_image}" class="img" alt="...">
                        </div>
                      <div class="">
                        <a href="/app/candidate-success-stories/${item.name}">
                            <h5 class="card-title">${item?.full_name}</h5>
                        </a>
                        <h6 class="card-subtitle mb-2 text-muted">${item?.achievement_after_training}</h6>
                      </div>
                    </div>
                <div class="mt-4">
                    <span style="font-size: 25px;display:block;line-height:0px;color:#264796;">&#x275D;</span>
                    <span class="card-text">${item?.remarks_of_the_training}</span>
                </div>
              </div>
            </div>
            </div>
        `;
    });

   root_element.querySelectorAll('.success_card_body').forEach((element)=>{
       element.innerHTML = card_titles
   });
});

style:
.success_card_body{
    gap:15px;
    animation: scroll 14s linear infinite;
}

.img{
  width: 3rem;
  height: 3rem;
  border-radius: 9999px;
  box-shadow: 0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05);
  object-fit: cover;
}

.scroll-container-success{
    position: relative;
    overflow: hidden;
    gap:15px;
    scroll-behavior: smooth;
  /*  mask-image: linear-gradient(*/
  /*      var(--mask-direction, to right),*/
  /*      hsl(0 0% 0% / 0),*/
  /*      hsl(0 0% 0% / 1) 2%,*/
  /*      hsl(0 0% 0% / 1) 98%,*/
  /*      hsl(0 0% 0% / 0)*/
  /*);*/
}

@keyframes scroll {
    0% {
        transform: translateX(0);
    }
    100% {
        transform: translateX(calc(-100% - 15px));
    }
}

/* Repeat the content to make the scroll effect continuous */
#success_card_body::after {
    content: attr(data-content);
    display: block;
}

.scroll-container-success:hover .success_card_body {
    animation-play-state: paused;
}
 @keyframes fade {
  to {
    opacity: 0;
    visibility: hidden;
  }
}
