script:
async function callAPI(options) {
    return new Promise((resolve, reject) => {
        frappe.call({
            ...options,
            callback: async function (response) {
                resolve(response?.message || response?.value)
            }
        });
    });
}

callAPI({
    method: 'aisect.services.api.success_story_data',
    freeze: true,
    freeze_message: __("Getting"),
}).then(forms_lists => {
    console.log(forms_lists);
    var card_titles = ``;
    forms_lists.forEach((item) => {
        card_titles += `
            <div>
                <div class="card" style="min-width: 540px; width:540px; min-height:200px;">
              <div class="card-body no-gutters">
                <div class="d-flex  align-items-center" style="gap:20px;">
                    <div class="">
                  <img src="${item?.candidate_image}" class="img" alt="...">      
                    </div>
                  <div class="">
                      <h5 class="card-title">${item?.full_name}</h5>
                    <h6 class="card-subtitle mb-2 text-muted">${item?.achievement_after_training}</h6>
                  </div>
                </div>
                <div class="mt-3">
                    <p style="font-size: 25px;">"</p>
                    <p class="card-text">${item?.remarks_of_the_training}</p>
                </div>
              </div>
            </div>
            </div>
        `;
    });

   root_element.getElementById('success_card_body').innerHTML = card_titles;
});

style:
#success_card_body{
    gap:15px;
    animation: scroll 15s linear infinite;

}

.img{
    width: 4rem;
  height: 4rem;
  border-radius: 9999px;
  box-shadow: 0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05);
  object-fit: contain;
}

.scroll-container-success{
    position: relative;
     overflow: hidden;
}

@keyframes scroll {
    0% {
        transform: translateX(0);
    }
    100% {
        transform: translateX(-100%);
    }
}

/* Repeat the content to make the scroll effect continuous */
#success_card_body::after {
    content: attr(data-content);
    display: block;
}

.scroll-container-success:hover #success_card_body {
    animation-play-state: paused;
}

