async function callAPI(options) {
  return new Promise((resolve, reject) => {
    frappe.call({
      ...options,
      callback: async function (response) {
        resolve(response?.message || response?.value)
      }
    });
  })
}

callAPI({
  method: 'aisect.api.candidate_placement_ging',
  freeze: true,
  freeze_message: __("Getting"),
}).then(forms_lists => {

    var fpo_count = root_element.getElementById('fpo_count');
    fpo_count.innerHTML =  forms_lists !== undefined ? forms_lists[0].candidate_count : 0; 
});
